package io.zaaim.arindexer.service;

import io.zaaim.arindexer.model.Document;
import io.zaaim.arindexer.model.SearchResult;
import io.zaaim.arindexer.util.IndexMaps;
import io.zaaim.arindexer.util.TFIDFVector;

import java.util.*;
import java.util.stream.Collectors;

/**
 * Service for searching documents using TF-IDF similarity
 */
public class SearchService {
    private final IndexService indexService;

    public SearchService(IndexService indexService) {
        this.indexService = indexService;
    }

    /**
     * Search in a specific index
     */
    public List<SearchResult> search(String query, String indexName, int limit) {
        if (query == null || query.isBlank()) {
            return new ArrayList<>();
        }

        IndexMaps indexMaps = indexService.getIndexMaps(indexName);
        Map<String, TFIDFVector> vectors = indexService.getTFIDFVectors(indexName);

        if (indexMaps == null || vectors == null || vectors.isEmpty()) {
            return new ArrayList<>();
        }

        // Build query vector
        TFIDFVector queryVector = buildQueryVector(query, indexName);

        if (queryVector.getMagnitude() == 0) {
            return new ArrayList<>();
        }

        // Calculate similarity scores
        Map<String, Double> scores = new HashMap<>();
        for (String docId : vectors.keySet()) {
            TFIDFVector docVector = vectors.get(docId);
            double similarity = queryVector.cosineSimilarity(docVector);
            if (similarity > 0) {
                scores.put(docId, similarity);
            }
        }

        // Sort by score descending and build results
        return scores.entrySet().stream()
                .sorted((a, b) -> Double.compare(b.getValue(), a.getValue()))
                .limit(limit)
                .map(entry -> {
                    String docId = entry.getKey();
                    double score = entry.getValue();
                    Document doc = indexService.getDocument(docId, indexName);

                    return new SearchResult(
                            docId,
                            doc.getTitle() != null ? doc.getTitle() : "",
                            truncateContent(doc.getContent(), 200),
                            score,
                            indexName
                    );
                })
                .collect(Collectors.toList());
    }

    /**
     * Search with filter
     */
    public List<SearchResult> searchWithFilter(String query, String indexName, 
                                                SearchFilter filter, int limit) {
        List<SearchResult> results = search(query, indexName, limit * 2);

        if (filter == null) {
            return results.stream().limit(limit).collect(Collectors.toList());
        }

        return results.stream()
                .filter(filter::matches)
                .limit(limit)
                .collect(Collectors.toList());
    }

    /**
     * Build TF-IDF vector for query
     */
    private TFIDFVector buildQueryVector(String query, String indexName) {
        IndexMaps indexMaps = indexService.getIndexMaps(indexName);

        // Tokenize query using the same stemming as during indexing
        Set<String> queryTokens = indexMaps != null ? 
                indexMaps.getAllTerms().stream()
                        .filter(term -> query.toLowerCase().contains(term))
                        .collect(Collectors.toSet()) : new HashSet<>();

        Map<String, Double> termWeights = new HashMap<>();

        if (!queryTokens.isEmpty()) {
            for (String term : queryTokens) {
                int tf = 1;
                double idf = indexMaps.getIDF(term);
                double tfidf = tf * idf;
                termWeights.put(term, tfidf);
            }
        }

        return new TFIDFVector("query", termWeights);
    }

    /**
     * Get related documents
     */
    public List<SearchResult> getRelatedDocuments(String docId, String indexName, int limit) {
        Map<String, TFIDFVector> vectors = indexService.getTFIDFVectors(indexName);

        if (vectors == null || !vectors.containsKey(docId)) {
            return new ArrayList<>();
        }

        TFIDFVector queryVector = vectors.get(docId);

        Map<String, Double> scores = new HashMap<>();
        for (String otherDocId : vectors.keySet()) {
            if (!otherDocId.equals(docId)) {
                TFIDFVector otherVector = vectors.get(otherDocId);
                double similarity = queryVector.cosineSimilarity(otherVector);
                if (similarity > 0) {
                    scores.put(otherDocId, similarity);
                }
            }
        }

        return scores.entrySet().stream()
                .sorted((a, b) -> Double.compare(b.getValue(), a.getValue()))
                .limit(limit)
                .map(entry -> {
                    String relatedDocId = entry.getKey();
                    double score = entry.getValue();
                    Document doc = indexService.getDocument(relatedDocId, indexName);

                    return new SearchResult(
                            relatedDocId,
                            doc.getTitle() != null ? doc.getTitle() : "",
                            truncateContent(doc.getContent(), 200),
                            score,
                            indexName
                    );
                })
                .collect(Collectors.toList());
    }

    private String truncateContent(String content, int maxLength) {
        if (content == null) {
            return "";
        }
        return content.length() > maxLength ? 
                content.substring(0, maxLength) + "..." : 
                content;
    }

    /**
     * Filter interface for custom search filtering
     */
    public interface SearchFilter {
        boolean matches(SearchResult result);
    }

package io.zaaim.arindexer.util;

import java.util.*;

/**
 * Represents a TF-IDF vector with term frequencies and IDF scores
 */
public class TFIDFVector {
    private final Map<String, Double> termWeights;
    private final String documentId;
    private final double magnitude;

    public TFIDFVector(String documentId, Map<String, Double> termWeights) {
        this.documentId = documentId;
        this.termWeights = new HashMap<>(termWeights);
        this.magnitude = calculateMagnitude();
    }

    private double calculateMagnitude() {
        double sum = 0;
        for (double weight : termWeights.values()) {
            sum += weight * weight;
        }
        return Math.sqrt(sum);
    }

    public String getDocumentId() {
        return documentId;
    }

    public Map<String, Double> getTermWeights() {
        return Collections.unmodifiableMap(termWeights);
    }

    public double getMagnitude() {
        return magnitude;
    }

    /**
     * Calculate cosine similarity with another vector
     */
    public double cosineSimilarity(TFIDFVector other) {
        if (this.magnitude == 0 || other.magnitude == 0) {
            return 0;
        }

        double dotProduct = 0;
        for (String term : this.termWeights.keySet()) {
            if (other.termWeights.containsKey(term)) {
                dotProduct += this.termWeights.get(term) * other.termWeights.get(term);
            }
        }

        return dotProduct / (this.magnitude * other.magnitude);
    }

    @Override
    public String toString() {
        return "TFIDFVector{" +
                "documentId='" + documentId + '\'' +
                ", terms=" + termWeights.size() +
                ", magnitude=" + magnitude +
                '}';
    }
}

package io.zaaim.arindexer.util;

import java.util.*;

/**
 * Manages term-to-document mappings and inverted index
 */
public class IndexMaps {
    // Map: term -> set of document IDs containing this term
    private final Map<String, Set<String>> invertedIndex;

    // Map: term -> document ID -> term frequency
    private final Map<String, Map<String, Integer>> termDocumentFrequency;

    // Map: term -> IDF value
    private final Map<String, Double> idfValues;

    // Map: document ID -> list of terms
    private final Map<String, Set<String>> documentTerms;

    private int totalDocuments;

    public IndexMaps() {
        this.invertedIndex = new HashMap<>();
        this.termDocumentFrequency = new HashMap<>();
        this.idfValues = new HashMap<>();
        this.documentTerms = new HashMap<>();
        this.totalDocuments = 0;
    }

    public void addDocument(String docId, Set<String> terms) {
        totalDocuments++;
        documentTerms.put(docId, new HashSet<>(terms));

        for (String term : terms) {
            // Update inverted index
            invertedIndex.computeIfAbsent(term, k -> new HashSet<>()).add(docId);

            // Update term-document frequency
            termDocumentFrequency.computeIfAbsent(term, k -> new HashMap<>())
                    .merge(docId, 1, Integer::sum);
        }

        // Recalculate IDF values
        recalculateIDF();
    }

    public void removeDocument(String docId) {
        if (!documentTerms.containsKey(docId)) {
            return;
        }

        Set<String> terms = documentTerms.get(docId);
        for (String term : terms) {
            // Remove from inverted index
            Set<String> docs = invertedIndex.get(term);
            if (docs != null) {
                docs.remove(docId);
                if (docs.isEmpty()) {
                    invertedIndex.remove(term);
                    termDocumentFrequency.remove(term);
                    idfValues.remove(term);
                }
            }

            // Remove from term-document frequency
            Map<String, Integer> docFreq = termDocumentFrequency.get(term);
            if (docFreq != null) {
                docFreq.remove(docId);
            }
        }

        documentTerms.remove(docId);
        totalDocuments--;
        recalculateIDF();
    }

    private void recalculateIDF() {
        idfValues.clear();
        for (String term : invertedIndex.keySet()) {
            int docsWithTerm = invertedIndex.get(term).size();
            double idf = Math.log10((double) totalDocuments / docsWithTerm);
            idfValues.put(term, idf);
        }
    }

    public int getTermFrequency(String term, String docId) {
        Map<String, Integer> termFreqs = termDocumentFrequency.get(term);
        if (termFreqs == null) {
            return 0;
        }
        return termFreqs.getOrDefault(docId, 0);
    }

    public double getIDF(String term) {
        return idfValues.getOrDefault(term, 0.0);
    }

    public Set<String> getDocumentsWithTerm(String term) {
        Set<String> docs = invertedIndex.get(term);
        return docs != null ? Collections.unmodifiableSet(docs) : Collections.emptySet();
    }

    public Set<String> getTermsInDocument(String docId) {
        Set<String> terms = documentTerms.get(docId);
        return terms != null ? Collections.unmodifiableSet(terms) : Collections.emptySet();
    }

    public Set<String> getAllTerms() {
        return Collections.unmodifiableSet(invertedIndex.keySet());
    }

    public int getTotalDocuments() {
        return totalDocuments;
    }

    public int getDocumentFrequency(String term) {
        Set<String> docs = invertedIndex.get(term);
        return docs != null ? docs.size() : 0;
    }

    public void clear() {
        invertedIndex.clear();
        termDocumentFrequency.clear();
        idfValues.clear();
        documentTerms.clear();
        totalDocuments = 0;
    }

    @Override
    public String toString() {
        return "IndexMaps{" +
                "totalDocuments=" + totalDocuments +
                ", uniqueTerms=" + invertedIndex.size() +
                '}';
    }
}

package io.zaaim.arindexer.util;

import java.util.*;

/**
 * Represents a TF-IDF vector with term frequencies and IDF scores
 */
public class TFIDFVector {
    private final Map<String, Double> termWeights;
    private final String documentId;
    private final double magnitude;

    public TFIDFVector(String documentId, Map<String, Double> termWeights) {
        this.documentId = documentId;
        this.termWeights = new HashMap<>(termWeights);
        this.magnitude = calculateMagnitude();
    }

    private double calculateMagnitude() {
        double sum = 0;
        for (double weight : termWeights.values()) {
            sum += weight * weight;
        }
        return Math.sqrt(sum);
    }

    public String getDocumentId() {
        return documentId;
    }

    public Map<String, Double> getTermWeights() {
        return Collections.unmodifiableMap(termWeights);
    }

    public double getMagnitude() {
        return magnitude;
    }

    /**
     * Calculate cosine similarity with another vector
     */
    public double cosineSimilarity(TFIDFVector other) {
        if (this.magnitude == 0 || other.magnitude == 0) {
            return 0;
        }

        double dotProduct = 0;
        for (String term : this.termWeights.keySet()) {
            if (other.termWeights.containsKey(term)) {
                dotProduct += this.termWeights.get(term) * other.termWeights.get(term);
            }
        }

        return dotProduct / (this.magnitude * other.magnitude);
    }

    @Override
    public String toString() {
        return "TFIDFVector{" +
                "documentId='" + documentId + '\'' +
                ", terms=" + termWeights.size() +
                ", magnitude=" + magnitude +
                '}';
    }
}
